import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-eventos',
  templateUrl: './eventos.component.html',
  styleUrls: ['./eventos.component.scss'],
})
export class EventosComponent implements OnInit {

  public eventos: any = [];

  //Para utilizar conexão com o banco de dados vamos fazer uma referencia ao HTTP Client injetado pelo construtor
  //Para isso temos que lembrar de infromar no app-modules a importação do HTTP Client.

  constructor(private http:HttpClient) {}

  ngOnInit() {
    this.getEventos();
  }

  public getEventos(): void {

    //Forma Manual de mandar os dados:
    /*
    this.eventos = [
      {
        Tema: 'Angular',
        Local: 'Campo Grande',
      },
      {
        Tema: 'Angular e Suas Novidades',
        Local: 'São Paulo',
      },
      {
        Tema: 'SQL Server ao vivo',
        Local: 'Curitiba',
      },
    ];
    */

    //Forma automática de buscar os dados do Banco de Dados por meio da API
    this.http.get('https://localhost:5001/api/eventos').subscribe({ //esse subscrive se comporta como um CallBack retornando a resposta do consumo da api
      //Utilizamos 2 propriedades principais
      next: (v) => this.eventos = v,
      error: (e) => console.error(e),
      complete: () => console.info('complete')
      //response => this.eventos = response,
      //error => console.log(error),
    });

    /*
    // also recommended
    of([1,2,3]).subscribe({
        next: (v) => console.log(v),
        error: (e) => console.error(e),
        complete: () => console.info('complete')
    })
    */

  }
}
<h2>Eventos</h2>

<hr>

<form class="row row-cols-lg-auto g-3 align-items-center" action="">
  <div>
    <label class="visually-hidden" for="inlineFormInputGroupUsername"></label>
    <div class="input-group">
      <input type="text" class="form-control" placeholder="buscar" />
    </div>
  </div>
  <div>
    <button type="submit" class="btn btn-outline-success">Buscar</button>
  </div>
</form>

<hr>

<table class="table">
  <thead class="table-dark">
    <tr>
      <th class="text-center" scope="col">#</th>
      <th class="text-center" scope="col">Imagem</th>
      <th class="text-center" scope="col">Tema</th>
      <th class="text-center" scope="col">Local</th>
      <th class="text-center" scope="col">Data</th>
      <th class="text-center" scope="col">Qtd Pessoas</th>
      <th class="text-center" scope="col">Lote</th>
      <th class="text-center" scope="col">Opções</th>
    </tr>
  </thead>
  <tbody *ngIf="eventos && eventos.length">
    <tr *ngFor="let evento of eventos">
      <th class="text-center" scope="row">{{evento.eventoId}}</th>
      <td class="text-center">

        {{evento.imagemURL}}</td>
      <td class="text-rigth">{{evento.tema}}</td>
      <td class="text-rigth">{{evento.local}}</td>
      <td class="text-center">{{evento.dataEvento}}</td>
      <td class="text-center">{{evento.qtdPessoas}}</td>
      <td class="text-center">{{evento.lote}}</td>
      <td class="text-center"><button class="btn btn-primary btn-sm">Pronto</button> <button class="btn btn-success btn-sm">Confirmar</button></td>
    </tr>
  </tbody>
  <tfoot *ngIf="!eventos.length">
    <tr>
      <td colspan="8" class="text-center">
        <h4>Nenhum Evento Encontrado</h4>
      </td>
    </tr>
  </tfoot>
</table>
